package codechef_lib;

import java.util.Scanner;

public class BADMINTON {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		for (int h=0;h<t;h++) {
			int n=sc.nextInt();
			System.out.println((int)(n/2)+1);
		}

	}

}
