package codechef_lib;

import java.util.Scanner;

public class RATING {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		for (int i=1;i<=t;i++) {
			int s = sc.nextInt();
			System.out.println((-1)*(s+1));
		}

	}

}
